mapper.select:
select no, json_file
from items
where theme = #{theme}
and type = #{type}

select no, type, json_file, scale, canvas_availability, icon_file, trick_type
from items
where no = #{no}

select no, type, maching_item
from items
where no = #{no}

mapper.insert:
insert into set_items
(no, item_name, item_no, map_no, position, rotation, setting, RETRACTABLE)
values(
si_seq.nextval, #{item_name}, #{item_no}, #{map_no}, #{position}, #{rotation}, #{setting}, #{retractable}
)

mapper.update:
update set_items
set


	matching_item = #{matching_item}


	rotation = #{rotation}


	,position = #{position}


	,setting = #{setting}


	retractable = #{retractable}


where map_no = #{map_no}
and item_no = #{item_no}
and item_name = #{item_name}

mapper.select:
select * 
from set_items
where map_no = #{map_no}

	and item_name = #{item_name}

mapper.delete:
delete from set_items
where map_no = #{map_no}
and item_name = #{item_name}

mapper.select:
select no "canvas_no", img_file
from canvas
where 

no = #{no}


img_file = #{fullPath}
